						$subpage->setFastextLink("yellow", strtoupper($fastextLineSegments[3]));
						$subpage->setFastextLink("blue", strtoupper($fastextLineSegments[4]));
						
						$subpage->setFastextLink("index", strtoupper(substr($fastextLineSegments[6], 0, 3)));
